import re
import base64


base64_string = """data:image/png;base64,""" # without data:image/... prefix

# 1Ô∏è Remove data:image/...;base64, header if present
base64_string = re.sub(r"^data:image/.+;base64,", "", base64_string)

base64_string = base64_string.strip().replace("\n", "").replace(" ", "")

missing_padding = len(base64_string) % 4
if missing_padding:
    base64_string += "=" * (4 - missing_padding)

# image_bytes = base64.b64decode(base64_string)
image_bytes = base64.b64decode(base64_string)

with open("output.png", "wb") as f:
    f.write(image_bytes)

print("Image saved successfully")
